import React, { useState, useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { MdAdd, MdCheckCircle } from 'react-icons/md';
import api from '~/services/api';

import { deleteMatriculationRequest } from '~/store/modules/matriculation/actions';

import history from '~/services/history';

export default function Plans() {
  const dispatch = useDispatch();
  const [matriculations, setMatriculations] = useState([]);

  async function loadMatriculations(name) {
    const queryParamsName = name !== undefined ? `?name=${name}` : '';
    const response = await api.get(`matriculations${queryParamsName}`);

    setMatriculations(response.data);
  }

  useEffect(() => {
    loadMatriculations();
  }, []);

  function goTo(page) {
    history.push(`/${page}`);
  }

  function deleteMatriculation(id, name) {
    if (window.confirm(`Apagar o cadastro do aluno ${name} (id ${id})?`)) {
      dispatch(deleteMatriculationRequest(id));
    }
    loadMatriculations();
  }

  return (
    <>
      <header>
        <h1>Gerenciando matrículas</h1>
        <aside>
          <button type='button' onClick={() => goTo('matriculationsform')}>
            <MdAdd size={16} color='#fff' /> CADASTRAR
          </button>
        </aside>
      </header>
      <div>
        <table>
          <thead>
            <td>ALUNO</td>
            <td>EMAIL</td>
            <td>INÍCIO</td>
            <td>TÉRMINO</td>
            <td>ATIVA</td>
            <td>AÇÕES</td>
          </thead>
          <tbody>
            {matriculations.map(matriculation => (
              <tr key={matriculation.id}>
                <td>Estudante {matriculation.student_id}</td>
                <td>email</td>
                <td>Plano {matriculation.plan_id}</td>
                <td>{matriculation.start_date}</td>
                <td>{matriculation.end_date}</td>
                <td>
                  <MdCheckCircle size={16} color='#16B836' />
                </td>
                <td>
                  <button
                    type='button'
                    className='blueMinimalButton'
                    onClick={() =>
                      goTo(`matriculationsform?id=${matriculation.id}`)
                    }
                  >
                    editar
                  </button>
                  <button
                    type='button'
                    onClick={() =>
                      deleteMatriculation(matriculation.id, matriculation.name)
                    }
                    className='redMinimalButton'
                  >
                    apagar
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </>
  );
}
