import { takeLatest, call, put, all } from 'redux-saga/effects';
import { toast } from 'react-toastify';
import api from '~/services/api';

// import { request } from './actions';

export function* toCreate({ payload }) {
  try {
    const { page, rest } = payload.data;
    yield call(api.post, page, rest);

    toast.success('Aluno matriculado com sucesso!');
  } catch (err) {
    toast.error('Erro ao atualizar dados!');
    // yield put(failure(payload.page, 'CREATE', payload.data));
  }
}

export function* toUpdate({ payload }) {
  try {
    const { page, data } = payload;
    const { id, rest } = data;
    yield call(api.put, `/${page}/${id}`, rest);

    toast.success('Matrícula atualizada com sucesso!');
  } catch (err) {
    toast.error('Erro ao editar matrcícula!');
    console.tron.log(payload);
    // yield put(failure(payload.page, 'UPDATE', payload.data));
  }
}

export function* toDelete({ payload }) {
  console.tron.log(payload);
  try {
    const { page, data } = payload;
    yield call(api.delete, page, { data: { id: String(data) } });

    toast.success('Matrícula deletada com sucesso!');
  } catch (err) {
    toast.error('Erro ao deletar matrícula!');
  }
}

export default all([
  takeLatest('CREATE', toCreate),
  takeLatest('UPDATE', toUpdate),
  takeLatest('DELETE', toDelete),
]);
