import { takeLatest, call, put, all } from 'redux-saga/effects';
import { toast } from 'react-toastify';
import api from '~/services/api';

import { newPlanFailure, updatePlanFailure } from './actions';

export function* updatePlan({ payload }) {
  try {
    const { id, ...rest } = payload.data;
    const { title, duration, price } = rest;

    yield call(api.put, `/plans/${id}`, { title, duration, price });

    toast.success('Plano atualizado com sucesso!');
  } catch (err) {
    toast.error('Erro ao atualizar dados!');
    yield put(updatePlanFailure());
  }
}

export function* newPlan({ payload }) {
  try {
    const { title, duration, price } = payload.data;
    yield call(api.post, `plans`, {
      title,
      duration: parseFloat(duration),
      price: parseFloat(price),
    });

    toast.success('Plano cadastrado com sucesso!');
  } catch (err) {
    toast.error('Erro ao inserir dados!');
  }
}

export function* deletePlan({ payload }) {
  try {
    const { data } = payload;
    yield call(api.delete, `plans`, { data: { id: String(data) } });

    toast.success('Plano deletado com sucesso!');
  } catch (err) {
    toast.error('Erro ao deletar plano!');
  }
}

export default all([
  takeLatest('@plan/UPDATE_PLAN_REQUEST', updatePlan),
  takeLatest('@plan/NEW_PLAN_REQUEST', newPlan),
  takeLatest('@plan/DELETE_PLAN_REQUEST', deletePlan),
]);
