import React, { useState, useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { MdArrowBack, MdCheck } from 'react-icons/md';
import api from '~/services/api';

import {
  newPlanRequest,
  updatePlanRequest,
} from '~/store/modules/plan/actions';

import history from '~/services/history';

export default function PlansForm() {
  const dispatch = useDispatch();

  const [plan, setPlan] = useState([]);
  const { title, duration, price } = plan;
  console.tron.log(plan);
  const params = new URL(document.location).searchParams;
  const planId = params.get('id') ? params.get('id') : '';

  useEffect(() => {
    async function planData() {
      const response = await api.get(`plans/${planId}`);

      setPlan(response.data);
    }

    if (planId !== '') {
      planData();
    }
  }, [planId]);

  function handleSubmit(planFormData) {
    const { title, duration, price } = planFormData;
    const action = planId
      ? updatePlanRequest({
          planId,
          title: 'Novo EDIT',
          duration: 3,
          price: 109,
        })
      : newPlanRequest({ title, duration, price });
    dispatch(action);
  }

  function goTo(page) {
    history.push(`/${page}`);
  }

  return (
    <>
      <header>
        <h1>Cadastro de plano</h1>
        <aside>
          <button
            type='button'
            className='greyButton'
            onClick={() => goTo('plans')}
          >
            <MdArrowBack size={16} color='#ee4d64' /> VOLTAR
          </button>
          <button type='button'>
            <MdCheck
              onClick={() => handleSubmit(plan)}
              size={16}
              color='#fff'
            />{' '}
            SALVAR
          </button>
        </aside>
      </header>
      <div>
        <form>
          <label htmlFor='name'>NOME DO PLANO</label>
          <input
            onChange={event =>
              setPlan({
                title: event.target.value,
                duration,
                price,
              })
            }
            value={title}
            type='text'
            name='title'
            id='title'
          />
          <div>
            <div>
              <label htmlFor='name'>MESES DE DURAÇÃO</label>
              <input
                onChange={event =>
                  setPlan({
                    title,
                    duration: event.target.value,
                    price,
                  })
                }
                value={duration}
                type='text'
                name='duration'
                id='duration'
              />
            </div>
            <div>
              <label htmlFor='name'>PREÇO MENSAL (R$)</label>
              <input
                onChange={event =>
                  setPlan({
                    title,
                    duration,
                    price: event.target.value,
                  })
                }
                value={price}
                type='text'
                name='price'
                id='price'
              />
            </div>
            <div>
              <label htmlFor='name'>PREÇO TOTAL (R$)</label>
              <input
                disabled='disabled'
                value={duration * price}
                type='text'
                name='totalPrice'
                id='totalPrice'
              />
            </div>
          </div>
        </form>
      </div>
    </>
  );
}
