import produce from 'immer';

const INITIAL_STATE = {
  matriculations: [],
}; // Apenas preferência do Diego criar essa variável como objeto para escrever menos no parãmetro da função "auth" logo abaixo

export default function user(state = INITIAL_STATE, action) {
  return produce(state, draft => {
    switch (action.type) {
      case '@matriculation/UPDATE_MATRICULATION_SUCCESS': {
        draft.profile = action.payload.data;
        break;
      }
      case '@matriculation/DELETE_MATRICULATION_SUCCESS': {
        draft.matriculations = action.payload.data;
        break;
      }
      case '@matriculation/SIGN_OUT': {
        draft.profile = null;
        break;
      }
      default:
    }
  });
}
