import styled from 'styled-components';
import { darken, lighten } from 'polished';

export const Wrapper = styled.div`
  min-height: 100%;
  height: auto;
  background: #f5f5f5;
  padding-bottom: 20px;
`;

export const Container = styled.div`
  max-width: 1000px;
  margin: 30px auto;
  padding: 0 50px;

  display: flex;
  flex-direction: column;

  h1 {
    font-size: 24px;
    color: #444;
  }

  header {
    display: flex;
    justify-content: space-between;
    align-items: center;

    aside {
      display: flex;
      align-items: center;
    }

    button {
      display: flex;
      align-items: center;
      justify-content: center;
      padding: 0 10px;
      border: 0;
      height: 35px;
      min-width: 110px;
      width: auto;
      background: #ee4d64;
      border-radius: 4px;
      font-size: 14px;
      color: #fff;
      font-weight: bold;
      margin-right: 10px;

      &:hover {
        background: ${darken(0.1, '#ee4d64')};
      }

      &.greyButton {
        background: #f5f5f5;
        border: 1px solid #ee4d64;
        color: #ee4d64;

        &:hover {
          background: ${lighten(0.35, '#ee4d64')};
        }
      }
    }

    input {
      height: 35px;
      border-radius: 4px;
      border: 1px solid #ccc;
      color: rgba(0, 0, 0, 0.5);
      padding: 0 5px;

      &::placeholder {
        color: rgba(0, 0, 0, 0.5);
      }
    }

    strong {
      font-size: 24px;
      color: #fff;
      margin: 0 13px;
    }
  }

  div {
    background: #fff;
    padding: 20px;
    margin-top: 15px;
    border-radius: 4px;

    table {
      width: 100%;
      max-width: 850px;
      border-spacing: 0px;

      thead {
        td {
          color: #333;
          font-weight: bold;
          font-size: 14px;

          &.center {
            text-align: center;
          }
        }
      }

      & td:not(:first-child) {
        text-align: center;
      }

      & td:nth-child(2):last-child {
        text-align: right;
      }

      td {
        border-bottom: 1px solid #ddd;
        padding: 15px 0;
        color: #777;
        font-size: 14px;

        button {
          border: 0;
          background: 0;
          font-size: 14px;

          &.blueMinimalButton {
            color: #298dba;
          }
          &.redMinimalButton {
            color: #ee4d64;
            margin-left: 5px;
          }
        }
      }
    }
  }
`;
