import React, { useState, useMemo, useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { MdArrowBack, MdCheck } from 'react-icons/md';
import api from '~/services/api';

import {
  newPlanRequest,
  updatePlanRequest,
} from '~/store/modules/plan/actions';

import history from '~/services/history';

export default function Plan() {
  const dispatch = useDispatch();

  const [plan, setPlan] = useState([]);
  const { id, title, duration, price } = plan;

  const params = new URL(document.location).searchParams;
  const planId = params.get('id') ? params.get('id') : '';

  useEffect(() => {
    async function planData() {
      const response = await api.get(`plans/${planId}`);

      setPlan(response.data);
    }

    if (planId !== '') {
      planData();
    }
  }, [planId]);

  function handleSubmit(planFormData) {
    const action = planId
      ? updatePlanRequest(planFormData)
      : newPlanRequest(planFormData);
    dispatch(action);
  }

  function goTo(page) {
    history.push(`/${page}`);
  }

  return (
    <>
      <header>
        <h1>Cadastro de plano</h1>
        <aside>
          <button
            type='button'
            className='greyButton'
            onClick={() => goTo('plans')}
          >
            <MdArrowBack size={16} color='#ee4d64' /> VOLTAR
          </button>
          <button type='button'>
            <MdCheck
              onClick={() => handleSubmit(plan)}
              size={16}
              color='#fff'
            />{' '}
            SALVAR
          </button>
        </aside>
      </header>
      <div>
        <form>
          <label htmlFor='name'>NOME DO PLANO</label>
          <input
            onChange={event =>
              setPlan({
                id,
                title: event.target.value,
                duration,
                price,
              })
            }
            value={title}
            type='text'
            name='title'
            id='title'
          />
          <div>
            <div>
              <label htmlFor='name'>DURAÇÃO</label>
              <input
                onChange={event =>
                  setPlan({
                    id,
                    title,
                    duration: event.target.value,
                    price,
                  })
                }
                value={duration}
                type='text'
                name=''
                id=''
              />
            </div>
            <div>
              <label htmlFor='name'>PREÇO MENSAL</label>
              <input type='text' name='' id='' />
            </div>
            <div>
              <label htmlFor='name'>PREÇO TOTAL</label>
              <input
                disabled='disabled'
                value='R$ 0,00'
                type='text'
                name=''
                id=''
              />
            </div>
          </div>
        </form>
      </div>
    </>
  );
}
