import React, { useState, useEffect } from 'react';
import { useDispatch } from 'react-redux';

import AsyncSelect from 'react-select/async';

import { format, parseISO, addMonths } from 'date-fns';
import { utcToZonedTime } from 'date-fns-tz';
import pt from 'date-fns/locale/pt';

import { MdArrowBack, MdCheck } from 'react-icons/md';

import api from '~/services/api';

import { request } from '~/store/modules/data/actions';

import { colourOptions, stateOptions } from './data';

import history from '~/services/history';

export default function Dashboard() {
  const dispatch = useDispatch();

  const [students, setStudents] = useState([]);

  const [matriculation, setMatriculation] = useState([]);
  const {
    active,
    studentId,
    planId,
    price,
    studentName,
    planTitle,
    planDuration,
    start_dateFormated,
    end_dateFormated,
  } = matriculation;
  console.tron.log(matriculation);

  const [plans, setPlans] = useState([]);

  const params = new URL(document.location).searchParams;
  const matriculationId = params.get('id') ? params.get('id') : '';

  useEffect(() => {
    async function plansData() {
      const response = await api.get(`plans`);
      setPlans(response.data);
    }
    setMatriculation(plansData);

    async function matriculationData() {
      const response = await api.get(`matriculations/${matriculationId}`);

      const formatStartDate = format(
        parseISO(response.data.start_date),
        'yyyy-MM-dd',
        {
          locale: pt,
        }
      );
      const formatEndDate = format(
        parseISO(response.data.end_date),
        'yyyy-MM-dd',
        {
          locale: pt,
        }
      );

      const matriculationData = {
        active: response.data.active,
        matriculationId: response.data.id,
        studentId: response.data.student_id,
        studentName: response.data.students.name,
        planId: response.data.plan_id,
        planDuration: response.data.plans.duration,
        planTitle: response.data.plans.title,
        price: response.data.price,
        start_dateFormated: formatStartDate,
        end_dateFormated: formatEndDate,
      };

      setMatriculation(matriculationData);
    }

    if (matriculationId !== '') {
      matriculationData();
    }
  }, [matriculationId]);

  function goTo(page) {
    history.push(`/${page}`);
  }

  function formatEndDate(refDate, planDuration) {
    return format(addMonths(new Date(refDate), planDuration), 'yyyy-MM-dd');
  }

  function handleEvent(planValue, start_dateFormated) {
    setMatriculation({
      active,
      matriculationId,
      studentId,
      studentName,
      planId: planValue.id,
      planDuration: planValue.duration,
      planTitle: planValue.title,
      price: planValue.price * planValue.duration,
      start_dateFormated,
      end_dateFormated: formatEndDate(start_dateFormated, planValue.duration),
    });
  }

  function handleSubmit(formData) {
    const { studentId, planId, start_dateFormated } = formData;
    const action = matriculationId
      ? request('UPDATE', 'matriculations', {
          id: studentId,
          rest: { plan_id: planId, start_date: start_dateFormated },
        })
      : request('CREATE', 'matriculations', {
          student_id: studentId,
          plan_id: planId,
          start_date: start_dateFormated,
        });
    dispatch(action);
  }

  const filterColors = inputValue => {
    return stateOptions.filter(i =>
      i.label.toLowerCase().includes(inputValue.toLowerCase())
    );
  };

  const promiseOptions = inputValue =>
    new Promise(resolve => {
      setTimeout(() => {
        resolve(filterColors(inputValue));
      }, 1000);
    });

  const customStyles = {
    menu: (provided, state) => ({
      // ...provided,
      width: state.selectProps.width,
      // borderBottom: '1px dotted pink',
      color: state.selectProps.menuColor,
      padding: 20,
      border: '1px solid red',
    }),

    control: (_, { selectProps: { width } }) => ({
      width,
    }),

    singleValue: (provided, state) => {
      const opacity = state.isDisabled ? 0.5 : 1;
      const transition = 'opacity 300ms';

      return { ...provided, opacity, transition };
    },
  };

  return (
    <>
      <header>
        <h1>Cadastro de matrÃ­cula</h1>
        <aside>
          <button
            type='button'
            className='greyButton'
            onClick={() => goTo('matriculations')}
          >
            <MdArrowBack size={16} color='#ee4d64' /> VOLTAR
          </button>
          <button type='button' onClick={() => handleSubmit(matriculation)}>
            <MdCheck size={16} color='#fff' /> SALVAR
          </button>
        </aside>
      </header>
      <div>
        <form>
          <label htmlFor='name'>NOME DO ALUNO</label>
          <AsyncSelect
            styles={customStyles}
            width='50px'
            menuColor='blue'
            cacheOptions
            defaultOptions
            loadOptions={promiseOptions}
          />
          <input
            type='text'
            name='name'
            id='name'
            disabled={matriculationId === '' ? '' : 'disabled'}
            value={studentName}
          />
          <div>
            <div>
              <label htmlFor='name'>PLANO</label>
              <select
                name='plans'
                onChange={event =>
                  handleEvent(
                    JSON.parse(event.target.value),
                    start_dateFormated
                  )
                }
              >
                {plans.map(plan => (
                  <option
                    selected={
                      matriculation.planId === plan.id ? 'selected' : ''
                    }
                    value={`{"id":${plan.id}, "title":"${plan.title}", "price":${plan.price}, "duration":${plan.duration}}`}
                  >
                    {`${plan.title} (R$${plan.price},00 / ${plan.duration} ${
                      plan.duration > 1 ? 'meses' : 'mÃªs'
                    })`}
                  </option>
                ))}
              </select>
            </div>
            <div>
              <label htmlFor='name'>DATA DE INÃCIO</label>
              <input
                type='date'
                value={start_dateFormated}
                name='startDateInput'
                onChange={event =>
                  setMatriculation({
                    active,
                    matriculationId,
                    studentId,
                    planId,
                    planDuration,
                    price,
                    studentName,
                    planTitle,
                    start_dateFormated: event.target.value,
                    end_dateFormated: formatEndDate(
                      event.target.value,
                      planDuration
                    ),
                  })
                }
              />
            </div>
            <div>
              <label htmlFor='name'>DATA DE TÃRMINO</label>
              <input
                type='date'
                disabled
                name=''
                id=''
                value={end_dateFormated}
              />
            </div>
            <div>
              <label htmlFor='name'>VALOR TOTAL</label>
              <input
                disabled='disabled'
                value={price}
                type='text'
                name=''
                id=''
              />
            </div>
          </div>
        </form>
      </div>
    </>
  );
}
