import React, { useState, useMemo, useEffect } from 'react';
import { useDispatch } from 'react-redux';
import {
  format,
  parseISO,
  addMonths,
  getDay,
  getDate,
  getMonth,
  getYear,
} from 'date-fns';
import { utcToZonedTime } from 'date-fns-tz';
import pt from 'date-fns/locale/pt';
import { MdArrowBack, MdCheck } from 'react-icons/md';
import api from '~/services/api';

import { request } from '~/store/modules/data/actions';

import history from '~/services/history';

export default function Dashboard() {
  const dispatch = useDispatch();

  const [matriculation, setMatriculation] = useState([]);
  const {
    active,
    studentId,
    planId,
    price,
    studentName,
    planTitle,
    planDuration,
    start_date,
    start_dateFormated,
    end_date,
    end_dateFormated,
    newEndDate,
  } = matriculation;

  const [plans, setPlans] = useState([]);

  const params = new URL(document.location).searchParams;
  const matriculationId = params.get('id') ? params.get('id') : '';

  useEffect(() => {
    async function plansData() {
      const response = await api.get(`plans`);
      setPlans(response.data);
    }
    setMatriculation(plansData);

    async function matriculationData() {
      const response = await api.get(`matriculations/${matriculationId}`);

      const formatStartDate = format(
        parseISO(response.data.start_date),
        'yyyy-MM-dd',
        {
          locale: pt,
        }
      );

      const matriculationData = {
        active: response.data.active,
        matriculationId: response.data.id,
        studentId: response.data.student_id,
        planId: response.data.plan_id,
        price: response.data.price,
        studentName: response.data.students.name,
        planTitle: response.data.plans.title,
        planDuration: response.data.plans.duration,
        start_date: response.data.start_date,
        start_dateFormated: formatStartDate,
        end_date: response.data.end_date,
        end_dateFormated: format(
          parseISO(response.data.end_date),
          'dd/MM/yyyy',
          {
            locale: pt,
          }
        ),
      };

      setMatriculation(matriculationData);
    }

    if (matriculationId !== '') {
      matriculationData();
    }
  }, [matriculationId]);

  function handleSubmit(formData) {
    const { plan_id, start_date, end_date } = formData;
    const action = matriculationId
      ? request('UPDATE', 'matriculations', formData)
      : request('CREATE', 'matriculations', { plan_id, start_date, end_date });
    dispatch(action);
  }

  function goTo(page) {
    history.push(`/${page}`);
  }

  function formatEndDate(refDate, planDuration) {
    return format(addMonths(new Date(refDate), planDuration), 'dd/MM/yyyy');
  }

  function handleEvent(planValue, start_dateFormated) {
    setMatriculation({
      active,
      matriculationId,
      studentId,
      studentName,
      planId: planValue.id,
      planDuration: planValue.duration,
      planTitle: planValue.title,
      price: planValue.price * planValue.duration,
      start_dateFormated,
      start_date,
      newEndDate: formatEndDate(start_dateFormated, planValue.duration),
    });
  }

  return (
    <>
      <header>
        <h1>Cadastro de matrÃ­cula</h1>
        <aside>
          <button
            type='button'
            className='greyButton'
            onClick={() => goTo('matriculations')}
          >
            <MdArrowBack size={16} color='#ee4d64' /> VOLTAR
          </button>
          <button type='button' onClick={() => handleSubmit(matriculation)}>
            <MdCheck size={16} color='#fff' /> SALVAR
          </button>
        </aside>
      </header>
      <div>
        <form>
          <label htmlFor='name'>NOME DO ALUNO</label>
          <input
            type='text'
            name='name'
            id='name'
            disabled={matriculationId === '' ? '' : 'disabled'}
            value={studentName}
          />
          <div>
            <div>
              <label htmlFor='name'>PLANO</label>
              <select
                name='plans'
                onChange={event =>
                  handleEvent(
                    JSON.parse(event.target.value),
                    start_dateFormated
                  )
                }
              >
                {plans.map(plan => (
                  <option
                    selected={
                      matriculation.planId === plan.id ? 'selected' : ''
                    }
                    value={`{"id":${plan.id}, "title":"${plan.title}", "price":${plan.price}, "duration":${plan.duration}}`}
                  >
                    {`${plan.title} (R$${plan.price},00 / ${plan.duration} ${
                      plan.duration > 1 ? 'meses' : 'mÃªs'
                    })`}
                  </option>
                ))}
              </select>
            </div>
            <div>
              <label htmlFor='name'>DATA DE INÃCIO</label>
              <input
                type='date'
                value={start_dateFormated}
                name='startDateInput'
                onChange={event =>
                  setMatriculation({
                    active,
                    matriculationId,
                    studentId,
                    planId,
                    planDuration,
                    price,
                    studentName,
                    planTitle,
                    start_dateFormated: event.target.value,
                    start_date,
                    newEndDate: formatEndDate(event.target.value, planDuration),
                  })
                }
              />
            </div>
            <div>
              <label htmlFor='name'>DATA DE TÃRMINO</label>
              <input
                type='text'
                disabled='disabled'
                name=''
                id=''
                value={!newEndDate ? end_dateFormated : newEndDate}
              />
            </div>
            <div>
              <label htmlFor='name'>VALOR TOTAL</label>
              <input
                disabled='disabled'
                value={price}
                type='text'
                name=''
                id=''
              />
            </div>
          </div>
        </form>
      </div>
    </>
  );
}
