import { useDispatch } from 'react-redux';
import { request } from '~/store/modules/data/actions';

const dispatch = useDispatch();

export function handleSubmit(page, id, formData) {
  const action = id
    ? request('UPDATE', page, {
        id,
        formData,
      })
    : request('CREATE', page, formData);
  dispatch(action);
}
