import produce from 'immer';

const INITIAL_STATE = {
  students: [
    {
      id: 2,
      name: 'Aluno dois',
      email: 'segundo@aluno.com',
      age: 37,
      weight: '67',
      height: '1.71',
      createdAt: '2019-12-05T21:06:23.014Z',
      updatedAt: '2019-12-09T10:25:33.076Z',
    },
    {
      id: 3,
      name: 'Terceiro aluno',
      email: 'terceiro@aluno.com',
      age: 45,
      weight: '76',
      height: '1.75',
      createdAt: '2019-12-06T00:05:06.947Z',
      updatedAt: '2019-12-06T00:05:06.947Z',
    },
    {
      id: 1,
      name: 'Um aluno',
      email: 'primeiro@aluno.com',
      age: 26,
      weight: '71',
      height: '1.73',
      createdAt: '2019-12-05T18:48:51.044Z',
      updatedAt: '2019-12-06T12:56:55.868Z',
    },
  ],
}; // Apenas preferência do Diego criar essa variável como objeto para escrever menos no parãmetro da função "auth" logo abaixo

export default function user(state = INITIAL_STATE, action) {
  return produce(state, draft => {
    switch (action.type) {
      case '@student/UPDATE_STUDENT_SUCCESS': {
        draft.profile = action.payload.data;
        break;
      }
      case '@student/DELETE_STUDENT_SUCCESS': {
        draft.students = action.payload.data;
        console.tron.log(action.payload.data);
        break;
      }
      case '@student/SIGN_OUT': {
        draft.profile = null;
        break;
      }
      default:
    }
  });
}
