import React, { useState, useEffect } from 'react';
import { Input } from '@rocketseat/unform';
import { MdArrowBack, MdCheck } from 'react-icons/md';
import api from '~/services/api';

import history from '~/services/history';

export default function StudentsForm() {
  const [student, setStudent] = useState([]);

  const params = new URL(document.location).searchParams;
  const studentId = params.get('id') ? params.get('id') : '';

  useEffect(() => {
    async function studentData() {
      const response = await api.get(`students/${studentId}`);

      setStudent(response.data);
    }

    studentData();
  }, [studentId]);

  async function updateStudent(id, studentData) {
    await api.put(`students/${id}`, { studentData });
    history.push(`/students`);
  }

  function goTo(page) {
    history.push(`/${page}`);
  }

  return (
    <>
      <header>
        <h1>Cadastro de aluno</h1>
        <aside>
          <button
            type='button'
            className='greyButton'
            onClick={() => goTo('students')}
          >
            <MdArrowBack size={16} color='#fff' /> VOLTAR
          </button>
          <button type='button'>
            <MdCheck
              size={16}
              color='#fff'
              onClick={() => updateStudent(student.id)}
            />{' '}
            SALVAR
          </button>
        </aside>
      </header>
      <div>
        <form>
          <label htmlFor='name'>
            NOME COMPLETO
            <Input
              type='text'
              onChange={event => setStudent(event.target.value)}
              value={student.name}
              name='name'
              id='name'
            />
          </label>
          <label htmlFor='email'>
            E-MAIL
            <Input type='email' value={student.email} name='email' id='email' />
          </label>
          <div>
            <div>
              <label htmlFor='age'>
                IDADE
                <Input type='text' value={student.age} name='age' id='age' />
              </label>
            </div>
            <div>
              <label htmlFor='weight'>
                PESO
                <Input
                  type='text'
                  value={student.weight}
                  name='weight'
                  id='weght'
                />
              </label>
            </div>
            <div>
              <label htmlFor='height'>
                ALTURA
                <Input
                  type='text'
                  value={student.height}
                  name='height'
                  id='height'
                />
              </label>
            </div>
          </div>
        </form>
      </div>
    </>
  );
}
