  /* const [date, setDate] = useState(new Date());
  const dateFormatted = useMemo(
    () => format(date, "d 'de' MMMM", { locale: pt }),
    [date]
  ); */

  useEffect(() => {
    async function loadHelpOrders() {
      const response = await api.get('help-orders');
      console.tron.log('data', response.data);
    }

    loadHelpOrders();
  }, []);

  return (
    <>
      <header>
        <h1>Pedidos de auxílio</h1>
      </header>
      <div>
        <table>
          <thead>
            <td>ALUNO</td>
            <td />
          </thead>
          <tbody>
            <tr>
              <td>José Gustavo</td>
              <td>
                <button type='button' className='blueMinimalButton'>
                  responder
                </button>
              </td>
            </tr>
            <tr>
              <td>Aluno 15</td>
              <td>
                <button type='button' className='blueMinimalButton'>
                  responder
                </button>
              </td>
            </tr>
            <tr>
              <td>Aluno 7</td>
              <td>
                <button type='button' className='blueMinimalButton'>
                  responder
                </button>
              </td>
            </tr>
            <tr>
              <td>Aluno 3</td>
              <td>
                <button type='button' className='blueMinimalButton'>
                  responder
                </button>
              </td>
            </tr>
          </tbody>
        </table>
      </div>
    </>
  );
}
