import React, { useState, useEffect } from 'react';
import { Form, Input } from '@rocketseat/unform';
import { MdArrowBack, MdCheck } from 'react-icons/md';
import api from '~/services/api';

import history from '~/services/history';

export default function StudentsForm() {
  const [student, setStudent] = useState([]);
  const { id, name, email, age, weight, height } = student;

  const params = new URL(document.location).searchParams;
  const studentId = params.get('id') ? params.get('id') : '';

  useEffect(() => {
    async function studentData() {
      const response = await api.get(`students/${studentId}`);

      setStudent(response.data);
    }

    studentData();
  }, [studentId]);

  const [studentName, setStudentName] = useState([student.name]);
  const [studentEmail, setStudentEmail] = useState(email);
  const [studentAge, setStudentAge] = useState(age);
  const [studentWeight, setStudentWeight] = useState(weight);
  const [studentHeight, setStudentHeight] = useState(height);

  console.tron.log(studentName);

  function updateStudent(id, name, email) {
    setStudent({ id, name, email });
  }

  async function updateSubmit(id, studentUpdate) {
    await api.put(`students/${id}`, { studentUpdate });
  }

  function goTo(page) {
    history.push(`/${page}`);
  }

  return (
    <>
      <header>
        <h1>Cadastro de aluno</h1>
        <aside>
          <button
            type='button'
            className='greyButton'
            onClick={() => goTo('students')}
          >
            <MdArrowBack size={16} color='#fff' /> VOLTAR
          </button>
          <button type='button'>
            <MdCheck size={16} color='#fff' onClick={() => updateSubmit(id)} />{' '}
            SALVAR
          </button>
        </aside>
      </header>
      <div>
        <form>
          <label htmlFor='name'>
            NOME COMPLETO
            <Input
              type='text'
              onChange={event => setStudentName(event.target.value)}
              value={name}
              name='name'
              id='name'
            />
          </label>
          <label htmlFor='email'>
            E-MAIL
            <Input type='email' value={email} name='email' id='email' />
          </label>
          <div>
            <div>
              <label htmlFor='age'>
                IDADE
                <Input type='text' value={age} name='age' id='age' />
              </label>
            </div>
            <div>
              <label htmlFor='weight'>
                PESO
                <Input type='text' value={weight} name='weight' id='weght' />
              </label>
            </div>
            <div>
              <label htmlFor='height'>
                ALTURA
                <Input type='text' value={height} name='height' id='height' />
              </label>
            </div>
          </div>
        </form>
      </div>
    </>
  );
}
