import { takeLatest, call, put, all } from 'redux-saga/effects';
import { toast } from 'react-toastify';
import api from '~/services/api';

import { newStudentFailure, updateStudentFailure } from './actions';

export function* updateStudent({ payload }) {
  try {
    // const { name, email, avatar_id, ...rest } = payload.data;
    const { id, ...rest } = payload.data;

    yield call(api.put, `/students/${id}`, rest);

    toast.success('Cadastro do aluno atualizado com sucesso!');

    // yield put(updateStudentSuccess(payload.data));
  } catch (err) {
    toast.error('Erro ao atualizar dados!');
    yield put(updateStudentFailure());
  }
}

export function* newStudent({ payload }) {
  try {
    const { id, ...rest } = payload.data;
    // const { data } = payload;
    console.tron.log('resto');
    console.tron.log(rest);
    // yield call(api.post, `/students`, rest);

    toast.success('Aluno cadastrado com sucesso!');

    // yield put(updateStudentSuccess(payload.data));
  } catch (err) {
    toast.error('Erro ao atualizar dados!');
    yield put(newStudentFailure());
  }
}

export default all([
  // Quando a action '@user/UPDATE_PROFILE_REQUEST' for despachada, executar um saga chamado "updateStudent"
  takeLatest('@student/UPDATE_STUDENT_REQUEST', updateStudent),
  takeLatest('@student/NEW_STUDENT_REQUEST', newStudent),
]);
