import React, { useState, useMemo, useEffect } from 'react';
import api from '~/services/api';

export default function Dashboard() {
  const [helpOrders, setHelpOrders] = useState([]);

  /* const [date, setDate] = useState(new Date());
  const dateFormatted = useMemo(
    () => format(date, "d 'de' MMMM", { locale: pt }),
    [date]
  ); */

  useEffect(() => {
    async function loadHelpOrders() {
      const response = await api.get('help-orders');
      setHelpOrders(response.data);
    }

    loadHelpOrders();
  }, []);

  function myFunction(hiding, showing) {
    const toHide = document.getElementById(hiding);
    const toShow = document.getElementById(showing);

    toShow.style.display = 'block';
    toHide.style.display = 'none';
  }

  return (
    <>
      <header>
        <h1>Pedidos de aux√≠lio</h1>
      </header>
      <div>
        <form id='helpOrderForm' style={{ display: 'none' }}>
          <h2>Nome do aluno perguntou:</h2>
          <p>Lorem ispsum...</p>
          <textarea placeholder='Resposta da equipe Gympoint...' />
          <button
            type='button'
            onClick={() => myFunction('helpOrderForm', 'helpOrderTable')}
          >
            Voltar para a lista
          </button>
          <button type='button'>Responder agora</button>
        </form>
        <table id='helpOrderTable' style={{ width: '100%' }}>
          <thead>
            <td>ALUNO</td>
            <td />
          </thead>
          <tbody>
            {helpOrders.map(helpOrder => (
              <tr>
                <td>{helpOrder.student}</td>
                <td>
                  <button
                    onClick={() =>
                      myFunction('helpOrderTable', 'helpOrderForm')
                    }
                    type='button'
                    className='blueMinimalButton'
                  >
                    responder
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </>
  );
}
